import {
  GET_PROFILE,
  GET_PROFILES,
  PROFILE_LOADING,
  CLEAR_CURRENT_PROFILE,
  GET_PROFILE_COMMENT
} from '../actions/types';

const initialState = {
  profile: null,
  profiles: null,
  loading: false,
  comment: {}
};

export default function(state = initialState, action) {
  switch (action.type) {

  
    case PROFILE_LOADING:
      return {
        ...state,
        loading: true
      };
    case GET_PROFILE:
      return {
        ...state,
        profile: action.payload,
        loading: false
      };
    case GET_PROFILES:
      return {
        ...state,
        profiles: action.payload,
        loading: false
      };
    case CLEAR_CURRENT_PROFILE:
      return {
        ...state,
        profile: null
      };
    case GET_PROFILE_COMMENT: 
      return {
        ...state,
        comment: action.payload
      }
    default:
      return state;
  }
}
